"use strict";(self.webpackChunklearning=self.webpackChunklearning||[]).push([[6211],{6211:(n,t,h)=>{h.r(t),h.d(t,{physics:()=>i});const i=[{semester:1,grade:8,questions:[{id:41,extra:"",question:'<p>H\xe3y th\u1ea3o lu\u1eadn nh\xf3m v\xe0 th\u1ef1c hi\u1ec7n nhi\u1ec7m v\u1ee5 sau: H\xecnh v\u1ebd s\u01a1 \u0111\u1ed3 nguy\xean l\xed m\xe1y n\xe9n th\u1ee7y l\u1ef1c. H\xe3y v\u1eadn d\u1ee5ng t\xednh ch\u1ea5t truy\u1ec1n nguy\xean v\u1eb9n \xe1p su\u1ea5t theo m\u1ecdi h\u01b0\u1edbng c\u1ee7a ch\u1ea5t l\u1ecfng \u0111\u1ec3 gi\u1ea3i th\xedch t\u1ea1i sao khi ng\u01b0\u1eddi t\xe1c d\u1ee5ng m\u1ed9t l\u1ef1c nh\u1ecf v\xe0o pit \u2013 t\xf4ng nh\u1ecf l\u1ea1i n\xe2ng \u0111\u01b0\u1ee3c \xf4 t\xf4 \u0111\u1eb7t tr\xean pit \u2013 t\xf4ng l\u1edbn.\n\n</p><p><img alt=\'1\' src="/learn/ly/Baitap4_1.png" style="max-width:100% !important;"/></p>\n        ',options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p>\n              <img alt=\'1\' src="/learn/ly/Baitap4_1_1.png" style="max-width:100% !important;"/>\n              </p>'},{id:41,extra:"",question:"<p>H\xe3y t\xecm th\xeam v\xed d\u1ee5 trong \u0111\u1eddi s\u1ed1ng minh h\u1ecda \xe1p su\u1ea5t t\xe1c d\u1ee5ng v\xe0o ch\u1ea5t l\u1ecfng s\u1ebd \u0111\u01b0\u1ee3c ch\u1ea5t l\u1ecfng truy\u1ec1n \u0111i nguy\xean v\u1eb9n theo m\u1ecdi h\u01b0\u1edbng.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>\n              M\u1ed9t s\u1ed1 v\xed d\u1ee5 trong \u0111\u1eddi s\u1ed1ng minh h\u1ecda \xe1p su\u1ea5t t\xe1c d\u1ee5ng v\xe0o ch\u1ea5t l\u1ecfng s\u1ebd \u0111\u01b0\u1ee3c ch\u1ea5t l\u1ecfng truy\u1ec1n \u0111i nguy\xean v\u1eb9n theo m\u1ecdi h\u01b0\u1edbng.</br>\n              \u2013 \u0110\xe0i phun n\u01b0\u1edbc: ho\u1ea1t \u0111\u1ed9ng d\u1ef1a tr\xean nguy\xean t\u1eafc \xe1p su\u1ea5t t\xe1c d\u1ee5ng v\xe0o ch\u1ea5t l\u1ecfng s\u1ebd \u0111\u01b0\u1ee3c ch\u1ea5t l\u1ecfng truy\u1ec1n \u0111i nguy\xean v\u1eb9n theo m\u1ecdi h\u01b0\u1edbng. Khi m\xe1y b\u01a1m ch\xf9m h\xfat n\u01b0\u1edbc t\u1eeb b\u1ec3 ch\u1ee9a v\xe0 \u0111\u01b0a n\u01b0\u1edbc t\u1edbi v\xf2i phun. D\u01b0\u1edbi t\xe1c \u0111\u1ed9ng c\u1ee7a l\u1ef1c m\xe1y b\u01a1m t\u1ea1o ra \xe1p su\u1ea5t t\xe1c d\u1ee5ng v\xe0o ch\u1ea5t l\u1ecfng l\xe0m n\u01b0\u1edbc \u0111\u01b0\u1ee3c \u0111\u1ea9y l\xean tr\xean qua v\xf2i phun v\xe0o t\u1ea1o th\xe0nh c\xe1c ki\u1ec3u d\xe1ng nh\u01b0 \xfd mu\u1ed1n.</br>\n              \u2013 C\xe1c lo\u1ea1i b\xecnh/ \u1ea5m c\xf3 v\xf2i r\xf3t n\u01b0\u1edbc th\u01b0\u1eddng c\xf3 l\u1ed7 \u1edf ph\u1ea7n n\u1eafp \u0111\u1ec3 th\xf4ng v\u1edbi kh\xf4ng kh\xed gi\xfap t\u1ea1o ra l\u1ef1c \xe9p g\xe2y l\xean \xe1p su\u1ea5t t\xe1c d\u1ee5ng v\xe0o ch\u1ea5t l\u1ecfng s\u1ebd \u0111\u01b0\u1ee3c ch\u1ea5t l\u1ecfng truy\u1ec1n \u0111i nguy\xean v\u1eb9n theo m\u1ecdi h\u01b0\u1edbng v\xe0 \u0111\u1ea9y n\u01b0\u1edbc tho\xe1t ra kh\u1ecfi v\xf2i.\n              </p>"},{id:41,extra:"",question:'<p>S\u1eed d\u1ee5ng m\u1ed9t \u1ed1ng th\u1ee7y tinh h\u1edf hai \u0111\u1ea7u v\xe0 m\u1ed9t c\u1ed1c n\u01b0\u1edbc (H\xecnh 16.8). Nh\xfang \u1ed1ng th\u1ee7y tinh v\xe0o c\u1ed1c n\u01b0\u1edbc \u0111\u1ec3 n\u01b0\u1edbc d\xe2ng l\xean m\u1ed9t ph\u1ea7n c\u1ee7a \u1ed1ng, r\u1ed3i l\u1ea5y ng\xf3n tay b\u1ecbt k\xedn \u0111\u1ea7u tr\xean v\xe0 k\xe9o \u1ed1ng ra kh\u1ecfi n\u01b0\u1edbc. Quan s\xe1t xem n\u01b0\u1edbc c\xf3 ch\u1ea3y ra kh\u1ecfi \u1ed1ng hay kh\xf4ng. V\u1eabn gi\u1eef tay b\u1ecbt k\xedn \u0111\u1ea7u tr\xean c\u1ee7a \u1ed1ng v\xe0 nghi\xeang \u1ed1ng theo c\xe1c ph\u01b0\u01a1ng kh\xe1c nhau, khi \u0111\xf3 n\u01b0\u1edbc c\xf3 ch\u1ea3y ra kh\u1ecfi \u1ed1ng hay kh\xf4ng? Gi\u1ea3i th\xedch hi\u1ec7n t\u01b0\u1ee3ng.</p>\n              <p><img alt=\'1\' src="/learn/ly/Baitap4_2.png" style="max-width:100% !important;"/></p>\n\n        ',options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>\u2013 K\u1ebft qu\u1ea3 th\xed nghi\u1ec7m:</br>\n+ Khi nh\u1ea5c \u1ed1ng th\u1ee7y tinh ra kh\u1ecfi c\u1ed1c n\u01b0\u1edbc v\xe0 1 tay b\u1ecbt k\xedn \u0111\u1ea7u tr\xean c\u1ee7a \u1ed1ng th\xec n\u01b0\u1edbc kh\xf4ng ch\u1ea3y ra kh\u1ecfi \u1ed1ng.</br>\nGi\u1ea3i th\xedch: Do \xe1p su\u1ea5t kh\xf4ng kh\xed b\xean ngo\xe0i \u1ed1ng t\xe1c d\u1ee5ng v\xe0o n\u01b0\u1edbc t\u1eeb ph\xeda d\u01b0\u1edbi l\xean l\u1edbn h\u01a1n \xe1p su\u1ea5t c\u1ee7a n\u01b0\u1edbc b\xean trong \u1ed1ng n\xean n\u01b0\u1edbc kh\xf4ng ch\u1ea3y ra kh\u1ecfi \u1ed1ng.</br>\n+ V\u1eabn gi\u1eef tay b\u1ecbt k\xedn \u0111\u1ea7u tr\xean c\u1ee7a \u1ed1ng v\xe0 nghi\xeang \u1ed1ng theo c\xe1c ph\u01b0\u01a1ng kh\xe1c nhau, khi \u0111\xf3 n\u01b0\u1edbc c\u0169ng kh\xf4ng ch\u1ea3y ra kh\u1ecfi \u1ed1ng.</br>\nGi\u1ea3i th\xedch: Do \xe1p su\u1ea5t kh\xf4ng kh\xed b\xean ngo\xe0i \u1ed1ng t\xe1c d\u1ee5ng v\xe0o n\u01b0\u1edbc trong \u1ed1ng theo m\u1ecdi ph\xeda \u0111\u1ec1u nh\u01b0 nhau v\xe0 l\u1edbn h\u01a1n \xe1p su\u1ea5t c\u1ee7a n\u01b0\u1edbc b\xean trong \u1ed1ng n\xean n\u01b0\u1edbc kh\xf4ng ch\u1ea3y ra kh\u1ecfi \u1ed1ng.</p>"},{id:41,extra:"",question:"T\xecm m\u1ed9t s\u1ed1 v\xed d\u1ee5 ch\u1ee9ng t\u1ecf s\u1ef1 t\u1ed3n t\u1ea1i c\u1ee7a \xe1p su\u1ea5t kh\xed quy\u1ec3n.\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>M\u1ed9t s\u1ed1 v\xed d\u1ee5 ch\u1ee9ng t\u1ecf s\u1ef1 t\u1ed3n t\u1ea1i c\u1ee7a \xe1p su\u1ea5t kh\xed quy\u1ec3n.</br>\n\u2013 H\xfat b\u1edbt kh\xf4ng kh\xed trong h\u1ed9p s\u1eefa b\u1eb1ng gi\u1ea5y, ta th\u1ea5y v\u1ecf h\u1ed9p b\u1ecb b\u1eb9p theo nhi\u1ec1u ph\xeda.</br>\nGi\u1ea3i th\xedch: Khi h\xfat b\u1edbt kh\xf4ng kh\xed trong h\u1ed9p s\u1eefa, khi \u0111\xf3 \xe1p su\u1ea5t trong h\u1ed9p s\u1eefa nh\u1ecf h\u01a1n \xe1p su\u1ea5t kh\xed quy\u1ec3n b\xean ngo\xe0i h\u1ed9p n\xean v\u1ecf h\u1ed9p s\u1eefa b\u1ecb b\u1eb9p theo nhi\u1ec1u ph\xeda.</br>\n\u2013 G\xf3i bim bim ph\u1ed3ng to, khi b\xf3c ra b\u1ecb x\u1eb9p.</br>\nGi\u1ea3i th\xedch: Khi b\xf3c g\xf3i bim bim kh\xf4ng kh\xed tho\xe1t ra ngo\xe0i d\u1eabn t\u1edbi \xe1p su\u1ea5t kh\xf4ng kh\xed b\xean ngo\xe0i l\u1edbn h\u01a1n \xe1p su\u1ea5t kh\xf4ng kh\xed trong g\xf3i bim bim n\xean g\xf3i bim bim b\u1ecb x\u1eb9p theo nhi\u1ec1u ph\xeda.</p>"},{id:41,extra:"",question:"Em h\xe3y t\xecm v\xed d\u1ee5 v\xe0 m\xf4 t\u1ea3 hi\u1ec7n t\u01b0\u1ee3ng trong th\u1ef1c t\u1ebf v\u1ec1 s\u1ef1 t\u1ea1o th\xe0nh ti\u1ebfng \u0111\u1ed9ng trong tai khi thay \u0111\u1ed5i \xe1p su\u1ea5t \u0111\u1ed9t ng\u1ed9t.\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>\u2013 V\xed d\u1ee5 nh\u01b0 khi \u0111i xe \xf4 t\xf4 ho\u1eb7c xe m\xe1y khi ph\xf3ng nhanh, hay khi thang m\xe1y l\xean ho\u1eb7c \u0111i xu\u1ed1ng \u0111\u1ec1u g\xe2y n\xean ti\u1ebfng \u0111\u1ed9ng trong tai ho\u1eb7c tri\u1ec7u ch\u1ee9ng \xf9 tai.</br>\n\n\u2013 Gi\u1ea3i th\xedch: Khi \xe1p su\u1ea5t thay \u0111\u1ed5i \u0111\u1ed9t ng\u1ed9t th\xec v\xf2i tai th\u01b0\u1eddng kh\xf4ng ph\u1ea3n \u1ee9ng k\u1ecbp l\xe0m m\u1ea5t c\xe2n b\u1eb1ng \xe1p su\u1ea5t hai b\xean m\xe0ng nh\u0129, khi\u1ebfn m\xe0ng nh\u0129 b\u1ecb \u0111\u1ea9y v\u1ec1 ph\xeda c\xf3 \xe1p su\u1ea5t nh\u1ecf h\u01a1n, g\xe2y n\xean ti\u1ebfng \u0111\u1ed9ng trong tai ho\u1eb7c tri\u1ec7u ch\u1ee9ng \xf9 tai.</p>"},{id:41,extra:"",question:'<p>H\xecnh b\xean l\xe0 m\u1ed9t m\xe1y n\xe9n th\u1ee7y l\u1ef1c \u0111\u01b0\u1ee3c d\xf9ng \u0111\u1ec3 n\xe2ng \xf4 t\xf4 trong c\xe1c gara. Mu\u1ed1n c\xf3 m\u1ed9t l\u1ef1c n\xe2ng l\xe0 10000N t\xe1c d\u1ee5ng l\xean pit-t\xf4ngl\u1edbn, th\xec ph\u1ea3i t\xe1c d\u1ee5ng l\xean pit-t\xf4ng nh\u1ecf m\u1ed9t l\u1ef1c b\u1eb1ng bao nhi\xeau? Bi\u1ebft pit-t\xf4ng l\u1edbn c\xf3 di\u1ec7n t\xedch l\u1edbn g\u1ea5p 5 l\u1ea7n pit-t\xf4ng nh\u1ecf v\xe0 ch\u1ea5t l\u1ecfng c\xf3 th\u1ec3 truy\u1ec1n nguy\xean v\u1eb9n \xe1p su\u1ea5t t\u1eeb pit-t\xf4ng nh\u1ecf sang pit-t\xf4ng l\u1edbn.</p>\n              <p><img alt=\'1\' src="/learn/ly/Baitap4_3.png" style="max-width:100% !important;"/></p>\n        ',options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p><img alt=\'1\' src="/learn/ly/Baitap4_3_!.png" style="max-width:100% !important;"/></p>'},{id:41,extra:"",question:"<p>T\xe1c d\u1ee5ng m\u1ed9t l\u1ef1c f = 300N l\xean pit-t\xf4ng nh\u1ecf c\u1ee7a m\u1ed9t m\xe1y \xe9p d\xf9ng n\u01b0\u1edbc. Di\u1ec7n t\xedch pit-t\xf4ng nh\u1ecf l\xe0 25 cm2, di\u1ec7n t\xedch pit-t\xf4ng l\u1edbn l\xe0 150 cm2. T\xednh \xe1p su\u1ea5t t\xe1c d\u1ee5ng l\xean pit-t\xf4ng nh\u1ecf v\xe0 l\u1ef1c t\xe1c d\u1ee5ng l\xean pit-t\xf4ng l\u1edbn.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p><img alt=\'1\' src="/learn/ly/Baitap4_4_1.png" style="max-width:100% !important;"/></p>'},{id:41,extra:"",question:"<p>Ngu\u1eddi ta d\xf9ng m\u1ed9t c\xe1i k\xedch thu\u1ef7 l\u1ef1c \u0111\u1ec3 n\xe2ng m\u1ed9t v\u1eadt c\xf3 tr\u1ecdng l\u01b0\u1ee3ng P = 30000N. Khi \u0111\u1eb7t v\u1eadt n\xe0y l\xean pit-t\xf4ng l\u1edbn th\xec l\u1ef1c c\u1ea7n thi\u1ebft t\xe1c d\u1ee5ng l\xean pit-t\xf4ng nh\u1ecf l\xe0 f = 100N. M\u1ed7i l\u1ea7n n\xe9n xu\u1ed1ng pit-t\xf4ng nh\u1ecf di chuy\u1ec3n \u0111\u01b0\u1ee3c m\u1ed9t \u0111o\u1ea1n h = 30 cm. Sau 50 l\u1ea7n n\xe9n th\xec v\u1eadt \u0111\u01b0\u1ee3c n\xe2ng l\xean m\u1ed9t \u0111\u1ed9 cao l\xe0 bao nhi\xeau? B\u1ecf qua c\xe1c lo\u1ea1i ma s\xe1t.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p><img alt=\'1\' src="/learn/ly/Baitap4_5_1.png" style="max-width:100% !important;"/></p>\n              <p>Khi pitong nh\u1ecf \u0111i xu\u1ed1ng  m\u1ed9t \u0111o\u1ea1n 30cm, th\xec ph\u1ea7n th\u1ec3 t\xedch t\u1eeb b\xecnh nh\u1ecf chuy\u1ec3n sang b\xecnh l\u1edbn l\xe0 V1=s.h v\xe0 khi \u0111\xf3 b\xecnh l\u1edbn s\u1ebd nh\u1eadn th\xeam m\u1ed9t l\u01b0\u1ee3ng ch\u1ea5t l\u1ecfng c\xf3 th\u1ec3 t\xedch l\xe0 V2=S.H</br>\n              Ta c\xf3: V1=V2 => s.h= S.H => S/s=h/H =>H=h/(S/s)= 30/300=0.1 cm</br>\n              Sau 30 l\u1ea7n n\xe9n v\u1eadt \u0111\u01b0\u1ee3c n\xe2ng l\xean: 0.1*50=5 cm</p>'},{id:41,extra:"",question:'<p>M\u1ed9t phanh \xf4 t\xf4 d\xf9ng d\u1ea7u g\u1ed3m 2 xi lanh n\u1ed1i v\u1edbi nhau b\u1eb1ng m\u1ed9t \u1ed1ng nh\u1ecf d\u1eabn d\u1ea7u. Pit-t\xf4ng nh\u1ecf c\u1ee7a xi lanh \u1edf \u0111\u1ea7u b\xe0n \u0111\u1ea1p c\xf3 ti\u1ebft di\u1ec7n 4cm2, c\xf2n p\xedt t\xf4ng l\u1edbn n\u1ed1i v\u1edbi 2 m\xe1 phanh c\xf3 ti\u1ebft di\u1ec7n 8cm2. T\xe1c d\u1ee5ng l\xean b\xe0n \u0111\u1ea1p m\u1ed9t l\u1ef1c 100N. \u0110\xf2n b\u1ea9y c\u1ee7a b\xe0n \u0111\u1ea1p l\xe0m cho l\u1ef1c \u0111\u1ea9y t\xe1c d\u1ee5ng l\xean pit-t\xf4ng nh\u1ecf t\u0103ng l\xean 4 l\u1ea7n. T\xednh l\u1ef1c \u0111\xe3 truy\u1ec1n \u0111\u1ebfn m\xe1 phanh.</p><p><img alt=\'1\' src="/learn/ly/Baitap4_6.png" style="max-width:100% !important;"/></p>\n        ',options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>\n              \u2013 \xc1p l\u1ef1c t\xe1c d\u1ee5ng l\xean pit-t\xf4ng l\xe0:  f1 = 4.F = 4.100 = 400(N)</br>\n              \u2013 Khi \u0111\xf3 \xe1p su\u1ea5t l\xean pit-t\xf4ng b\xe0n \u0111\u1ea1p l\xe0: p1 = f1/s1 = 400/4 = 100(N/cm2)</br>\n                \u2013 \xc1p su\u1ea5t n\xe0y \u0111\u01b0\u1ee3c truy\u1ec1n nguy\xean v\u1eb9n \u0111\u1ebfn pit-t\xf4ng l\u1edbn n\xean ta c\xf3: p2 = p1 = 100(N/cm2)</br>\n                \u2013 L\u1ef1c t\xe1c d\u1ee5ng l\xean m\xe1 phanh l\xe0: f2 = p2.s2 = 100.8 = 800(N)</br>\n              </p>"},{id:41,extra:"",question:"<p>M\u1ed9t ng\u01b0\u1eddi tr\u01b0\u1edfng th\xe0nh n\u1eb7ng 60kg cao 1,6 m th\xec c\xf3 di\u1ec7n t\xedch c\u01a1 th\u1ec3 trung b\xecnh l\xe0 1,6m2 h\xe3y t\xednh \xe1p l\u1ef1c c\u1ee7a kh\xed quy\u1ec3n t\xe1c d\u1ee5ng l\xean ng\u01b0\u1eddi \u0111\xf3 trong \u0111i\u1ec1u ki\u1ec7n ti\xeau chu\u1ea9n. Bi\u1ebft tr\u1ecdng l\u01b0\u1ee3ng ri\xeang c\u1ee7a Mercury (th\u1ee7y ng\xe2n) l\xe0 136000 N/m3. T\u1ea1i sao ng\u01b0\u1eddi ta c\xf3 th\u1ec3 ch\u1ecbu \u0111\u1ef1ng \u0111\u01b0\u1ee3c \xe1p l\u1ef1c l\u1edbn nh\u01b0 v\u1eady m\xe0 kh\xf4ng h\u1ec1 c\u1ea3m th\u1ea5y t\xe1c d\u1ee5ng c\u1ee7a \xe1p l\u1ef1c n\xe0y?</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>\xc1p su\u1ea5t kh\xed quy\u1ec3n ti\xeau chu\u1ea9n \u0111\u01b0\u1ee3c t\xednh b\u1eb1ng c\xf4ng th\u01b01c: p = d.h = 136000.0.76 = 103360 (N/m2)</br>\n              trong \u0111\xf3: d l\xe0 tr\u1ecdng l\u01b0\u1ee3ng ri\xeang c\u1ee7a th\u1ee7y ng\xe2n, h l\xe0 chi\u1ec1u cao c\u1ed9t th\u1ee7y ng\xe2n trong \u1ed1ng thu\u1ef7 ng\xe2n.</br>\n                \xc1p l\u1ef1c t\xe1c d\u1ee5ng l\xean ng\u01b0\u1eddi l\xe0: F = p.S = 103360.1.6 = 165376 (N)</br>\n                S\u1edf d\u0129 ng\u01b0\u1eddi ta c\xf3 th\u1ec3 ch\u1ecbu \u0111\u1ef1ng \u0111\u01b0\u1ee3c v\xe0 kh\xf4ng c\u1ea3m th\u1ea5y t\xe1c d\u1ee5ng c\u1ee7a \xe1p l\u1ef1c n\xe0y v\xec b\xean trong c\u01a1 th\u1ec3 c\u0169ng c\xf3 kh\xf4ng kh\xed n\xean \xe1p l\u1ef1c t\xe1c d\u1ee5ng t\u1eeb b\xean ngo\xe0i v\xe0 b\xean trong c\xe2n b\u1eb1ng nhau.\n              </p>"},{id:41,extra:"",question:"<p>\u0110i\xea\u0300u na\u0300o sau \u0111\xe2y \u0111u\u0301ng khi no\u0301i v\xea\u0300 a\u0301p su\xe2\u0301t ch\xe2\u0301t lo\u0309ng?</br>\n              A. Ch\xe2\u0301t lo\u0309ng g\xe2y a\u0301p su\xe2\u0301t theo mo\u0323i ph\u01b0\u01a1ng.</br>\nB. A\u0301p su\xe2\u0301t ta\u0301c du\u0323ng l\xean tha\u0300nh bi\u0300nh kh\xf4ng phu\u0323 thu\xf4\u0323c di\xea\u0323n ti\u0301ch bi\u0323 e\u0301p.</br>\nC. A\u0301p su\xe2\u0301t g\xe2y ra do tro\u0323ng l\u01b0\u01a1\u0323ng cu\u0309a ch\xe2\u0301t lo\u0309ng ta\u0301c du\u0323ng l\xean m\xf4\u0323t \u0111i\xea\u0309m ti\u0309 l\xea\u0323 nghi\u0323ch v\u01a1\u0301i \u0111\xf4\u0323 s\xe2u.</br>\nD. N\xea\u0301u cu\u0300ng \u0111\xf4\u0323 s\xe2u thi\u0300 a\u0301p su\xe2\u0301t nh\u01b0 nhau trong mo\u0323i ch\xe2\u0301t lo\u0309ng</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>A</p>"},{id:41,extra:"",question:"<p>\u0110i\xea\u0300u na\u0300o sau \u0111\xe2y sai khi no\u0301i v\xea\u0300 a\u0301p su\xe2\u0301t ch\xe2\u0301t lo\u0309ng?</br>\nA. Ch\xe2\u0301t lo\u0309ng g\xe2y a\u0301p su\xe2\u0301t theo mo\u0323i h\u01b0\u01a1\u0301ng.</br>\nB. A\u0301p su\xe2\u0301t ta\u0301c du\u0323ng l\xean tha\u0300nh bi\u0300nh phu\u0323c thu\xf4\u0323c va\u0300o di\xea\u0323n ti\u0301ch bi\u0323 e\u0301p.</br>\nC. A\u0301p su\xe2\u0301t g\xe2y ra do tro\u0323ng l\u01b0\u01a1\u0323ng cu\u0309a ch\xe2\u0301t lo\u0309ng ta\u0301c du\u0323ng l\xean m\xf4\u0323t \u0111i\xea\u0309m ti\u0309 l\xea\u0323 v\u01a1\u0301i \u0111\xf4\u0323 s\xe2u.</br>\nD. A\u0301p su\xe2\u0301t ta\u0323i nh\u01b0\u0303ng \u0111i\xea\u0309m tr\xean m\xf4\u0323t m\u0103\u0323t ph\u0103\u0309ng n\u0103\u0300m ngang trong ch\xe2\u0301t lo\u0309ng \u0111\u01b0\u0301ng y\xean la\u0300 kha\u0301c nhau.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"D"},{id:41,extra:"",question:"<p>A\u0301p su\xe2\u0301t ma\u0300 ch\xe2\u0301t lo\u0309ng ta\u0301c du\u0323ng l\xean m\xf4\u0323t \u0111i\xea\u0309m phu\u0323 thu\xf4\u0323c:</br>\nA. Kh\xf4\u0301i l\u01b0\u01a1\u0323ng l\u01a1\u0301p ch\xe2\u0301t lo\u0309ng phi\u0301a tr\xean.</br>\nB. Tro\u0323ng l\u01b0\u01a1\u0323ng l\u01a1\u0301p ch\xe2\u0301t lo\u0309ng phi\u0301a tr\xean.</br>\nC. Th\xea\u0309 ti\u0301ch l\u01a1\u0301p ch\xe2\u0301t lo\u0309ng phi\u0301a tr\xean.</br>\nD. \u0110\xf4\u0323 cao l\u01a1\u0301p ch\xe2\u0301t lo\u0309ng phi\u0301a tr\xean.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"D"},{id:41,extra:"",question:"<p>\u0110i\xea\u0300u na\u0300o sau \u0111\xe2y \u0111u\u0301ng khi no\u0301i v\xea\u0300 bi\u0300nh th\xf4ng nhau?</br>\nA. Trong bi\u0300nh th\xf4ng nhau ch\u01b0\u0301a cu\u0300ng m\xf4\u0323t ch\xe2\u0301t lo\u0309ng \u0111\u01b0\u0301ng y\xean, l\u01b0\u01a1\u0323ng ch\xe2\u0301t lo\u0309ng \u01a1\u0309 hai nha\u0301nh lu\xf4n kha\u0301c nhau.</br>\nB. Trong bi\u0300nh th\xf4ng nhau ch\u01b0\u0301a cu\u0300ng m\xf4\u0323t ch\xe2\u0301t lo\u0309ng \u0111\u01b0\u0301ng y\xean, m\u01b0\u0323c ch\xe2\u0301t lo\u0309ng \u01a1\u0309 hai nha\u0301nh co\u0301 th\u1ec3 kha\u0301c nhau.</br>\nC. Trong bi\u0300nh th\xf4ng nhau ch\u01b0\u0301a cu\u0300ng m\xf4\u0323t ch\xe2\u0301t lo\u0309ng \u0111\u01b0\u0301ng y\xean, kh\xf4ng t\xf4\u0300n ta\u0323i a\u0301p su\xe2\u0301t cu\u0309a ch\xe2\u0301t lo\u0309ng</br>\nD. Trong bi\u0300nh th\xf4ng nhau ch\u01b0\u0301a cu\u0300ng m\xf4\u0323t ch\xe2\u0301t lo\u0309ng \u0111\u01b0\u0301ng y\xean, ca\u0301c m\u01b0\u0323c ch\xe2\u0301t lo\u0309ng \u01a1\u0309 hai nha\u0301nh lu\xf4n co\u0301 cu\u0300ng m\xf4\u0323t \u0111\xf4\u0323 cao.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"D"},{id:41,extra:"",question:"<p>Hu\u0301t b\u01a1\u0301t kh\xf4ng khi\u0301 trong m\xf4\u0323t vo\u0309 h\xf4\u0323p \u0111\u01b0\u0323ng s\u01b0\u0303a b\u0103\u0300ng gi\xe2\u0301y, ta th\xe2\u0301y vo\u0309 h\xf4\u0323p gi\xe2\u0301y bi\u0323 be\u0323p la\u0323i la\u0300 vi\u0300:</br>\nA. vi\xea\u0323c hu\u0301t ma\u0323nh \u0111a\u0303 la\u0300m be\u0323p h\xf4\u0323p.</br>\nB. a\u0301p su\xe2\u0301t b\xean trong h\xf4\u0323p t\u0103ng l\xean la\u0300m cho h\xf4\u0323p bi\u0323 bi\xea\u0301n da\u0323ng.</br>\nC. a\u0301p su\xe2\u0301t b\xean trong h\xf4\u0323p gia\u0309m, a\u0301p su\xe2\u0301t khi\u0301 quy\xea\u0309n \u01a1\u0309 b\xean ngoa\u0300i h\xf4\u0323p l\u01a1\u0301n h\u01a1n la\u0300m no\u0301 be\u0323p.</br>\nD. khi hu\u0301t ma\u0323nh la\u0300m y\xea\u0301u ca\u0301c tha\u0300nh h\xf4\u0323p la\u0300m h\xf4\u0323p be\u0323p \u0111i.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"C"},{id:41,extra:"",question:"<p>Trong ca\u0301c hi\xea\u0323n t\u01b0\u01a1\u0323ng sau \u0111\xe2y, hi\xea\u0323n t\u01b0\u01a1\u0323ng na\u0300o KH\xd4NG do a\u0301p su\xe2\u0301t khi\u0301 quy\xea\u0309n g\xe2y ra?</br>\nA. M\xf4\u0323t c\xf4\u0301c \u0111\u01b0\u0323ng \u0111\xe2\u0300y n\u01b0\u01a1\u0301c \u0111ang \u0111\u01b0\u01a1\u0323c \u0111\xe2\u0323y b\u0103\u0300ng mi\xea\u0301ng bi\u0300a khi l\xf4\u0323n ng\u01b0\u01a1\u0323c c\xf4\u0301c thi\u0300 n\u01b0\u01a1\u0301c kh\xf4ng cha\u0309y ra ngoa\u0300i.</br>\nB. Con ng\u01b0\u01a1\u0300i co\u0301 th\xea\u0309 hi\u0301t kh\xf4ng khi\u0301 va\u0300o ph\xf4\u0309i.</br>\nC. Chu\u0301ng ta kho\u0301 ru\u0301t ch\xe2n ra kho\u0309i bu\u0300n.</br>\nD. V\xe2\u0323t r\u01a1i t\u01b0\u0300 tr\xean cao xu\xf4\u0301ng.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"D"},{id:41,extra:"",question:"<p>Pha\u0301t bi\xea\u0309u na\u0300o sau \u0111\xe2y la\u0300 \u0111u\u0301ng khi no\u0301i v\xea\u0300 a\u0301p su\xe2\u0301t khi\u0301 quy\xea\u0309n?</br>\nA. A\u0301p su\xe2\u0301t khi\u0301 quy\xea\u0309n ta\u0301c du\u0323ng theo mo\u0323i ph\u01b0\u01a1ng.</br>\nB. A\u0301p su\xe2\u0301t khi\u0301 quy\xea\u0309n b\u0103\u0300ng a\u0301p su\xe2\u0301t thu\u0309y ng\xe2n.</br>\nC. A\u0301p su\xe2\u0301t khi\u0301 quy\xea\u0309n chi\u0309 ta\u0301c du\u0323ng theo ph\u01b0\u01a1ng th\u0103\u0309ng \u0111\u01b0\u0301ng h\u01b0\u01a1\u0301ng t\u01b0\u0300 d\u01b0\u01a1\u0301i l\xean tr\xean.</br>\nD. A\u0301p su\xe2\u0301t khi\u0301 quy\xea\u0309n chi\u0309 ta\u0301c du\u0323ng theo ph\u01b0\u01a1ng th\u0103\u0309ng \u0111\u01b0\u0301ng h\u01b0\u01a1\u0301ng t\u01b0\u0300 tr\xean xu\xf4\u0301ng d\u01b0\u01a1\u0301i.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"A"},{id:41,extra:"",question:"<p>M\xf4\u0323t t\xe2\u0300u ng\xe2\u0300m \u0111ang di chuy\xea\u0309n d\u01b0\u01a1\u0301i bi\xea\u0309n. A\u0301p k\xea\u0301 \u0111\u0103\u0323t \u01a1\u0309 ngoa\u0300i vo\u0309 ta\u0300u chi\u0309 875000 N/m2, m\xf4\u0323t lu\u0301c sau a\u0301p k\xea\u0301 chi\u0309 1165000 N/m2. Nh\xe2\u0323n xe\u0301t na\u0300o sau \u0111\xe2y la\u0300 \u0111u\u0301ng?</br>\nA. Ta\u0300u \u0111ang l\u0103\u0323n xu\xf4\u0301ng.</br>\nB. Ta\u0300u \u0111ang chuy\xea\u0309n \u0111\xf4\u0323ng v\xea\u0300 phi\u0301a tr\u01b0\u01a1\u0301c theo ph\u01b0\u01a1ng ngang.</br>\nC. Ta\u0300u \u0111ang t\u01b0\u0300 t\u01b0\u0300 n\xf4\u0309i l\xean.</br>\nD. Ta\u0300u \u0111ang chuy\xea\u0309n \u0111\xf4\u0323ng lu\u0300i v\xea\u0300 ohias sau theo ph\u01b0\u01a1ng ngang.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>Theo \u0111\xea\u0300 ba\u0300i, ta co\u0301:</br>\n\u2013 A\u0301p su\xe2\u0301t ban \u0111\xe2\u0300u la\u0300 875000 N/m2.</br>\n\u2013 A\u0301p su\xe2\u0301t lu\u0301c sau la\u0300 1165000 N/m2.</br>\nTa co\u0301, a\u0301p su\xe2\u0301t p = d.h</br>\nTrong \u0111o\u0301: h la\u0300 \u0111\xf4\u0323 s\xe2u ti\u0301nh t\u01b0\u0300 m\u0103\u0323t thoa\u0301ng ch\xe2\u0301t lo\u0309ng \u0111\xea\u0301n \u0111i\xea\u0309m ti\u0301nh a\u0301p su\xe2\u0301t (m).</br>\nMa\u0300: a\u0301p su\xe2\u0301t lu\u0301c sau h\u01a1n a\u0301p su\xe2\u0301t ban \u0111\xe2\u0300u.</br>\nSuy ra \u0111\xf4\u0323 s\xe2u cu\u0309a ta\u0300u so v\u01a1\u0301i m\u0103\u0323t n\u01b0\u01a1\u0301c bi\xea\u0309n lu\u0301c sau l\u01a1\u0301n h\u01a1n ban \u0111\xe2\u0300u.</br>\nV\xe2\u0323y: ta\u0300u \u0111ang l\u0103\u0323n.</p>"},{id:41,extra:"",question:"<p>Ca\u0300ng l\xean cao kh\xf4ng khi\u0301 ca\u0300ng loa\u0303ng n\xean a\u0301p su\xe2\u0301t ca\u0300ng gia\u0309m. C\u01b0\u0301 l\xean cao 12m thi\u0300 a\u0301p su\xe2\u0301t khi\u0301 quy\xea\u0309n gia\u0309m khoa\u0309ng 1mmHg. A\u0301p su\xe2\u0301t khi\u0301 quy\xea\u0309n \u01a1\u0309 \u0111\xf4\u0323 cao 800m la\u0300 bao nhi\xeau? Bi\xea\u0301t ta\u0323i m\u0103\u0323t \u0111\xe2\u0301t kh\xed quy\xea\u0309n la\u0300 760mmHg.</br>\nA. 748 mmHg.</br>\nB. 693,3 mmHg.</br>\nC. 663 mmHg.</br>\nD. 826,7 mmHg.</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p><img alt=\'1\' src="/learn/ly/Baitap4_7_1.png" style="max-width:100% !important;"/></p>'},{id:41,extra:"",question:"<p>M\u1ed9t c\u0103n ph\xf2ng r\u1ed9ng 4m, d\xe0i 6m, cao 3m. Bi\u1ebft tr\u1ecdng l\u01b0\u1ee3ng ri\xeang c\u1ee7a kh\xf4ng kh\xed l\xe0 1,29 kg/m3. T\xednh tr\u1ecdng l\u01b0\u1ee3ng c\u1ee7a kh\xf4ng kh\xed trong ph\xf2ng.</br>\n              A. 500 N</br>\nB. 789,7 N</br>\nC. 928,8 N</br>\nD. 1000 N</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>\u2013 Th\u1ec3 t\xedch c\u1ee7a ph\xf2ng l\xe0:</br>\nV = 4.6.3 = 72 m3</br>\n\u2013 Kh\u1ed1i l\u01b0\u1ee3ng kh\xf4ng kh\xed trong ph\xf2ng l\xe0:</br>\nm = V.D = 72.1,29 = 92,88 kg</br>\n\u2013 Tr\u1ecdng l\u01b0\u1ee3ng c\u1ee7a kh\xf4ng kh\xed trong ph\xf2ng l\xe0:</br>\nP = 10.m = 10.92,88 = 928,8 N</p>"},{id:41,extra:"",question:"<p>Ng\u01b0\u1eddi ta \u0111\u1ed7 v\xe0o \u1ed1ng chia \u0111\u1ed9 m\u1ed9t l\u01b0\u1ee3ng th\u1ee7y tinh v\xe0 m\u1ed9t l\u01b0\u1ee3ng n\u01b0\u1edbc c\xf3 c\xf9ng kh\u1ed1i l\u01b0\u1ee3ng. Chi\u1ec1u cao c\u1ee7a t\u1ed5ng c\u1ed9ng 2 l\u1edbp ch\u1ea5t l\u1ecfng l\xe0 29.2 cm. T\xednh \xe1p su\u1ea5t c\xe1c ch\u1ea5t l\u1ecfng t\xe1c d\u1ee5ng l\xean \u0111\xe1y \u1ed1ng. V\u1edbi tr\u1ecdng l\u01b0\u1ee3ng ri\xeang c\u1ee7a th\u1ee7y nh\xe2n l\xe0 136000 M/m3 v\xe0 c\u1ee7a n\u01b0\u1edbc l\xe0 10000 M/m3. </p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>\n              G\u1ecdi h1, h2 l\xe0 chi\u1ec1u cao c\u1ee7a l\u1edbp th\u1ee7y ng\xe2n v\xe0 n\u01b0\u1edbc trong \u1ed1ng chia \u0111\u1ed9.</br>\n              Trong \u0111\xf3: h1 + h2 = 29,2 cm (1)</br>\n              Tr\u1ecdng l\u01b0\u1ee3ng c\u1ee7a th\u1ee7y ng\xe2n v\xe0 n\u01b0\u1edbc trong \u1ed1ng chia \u0111\u1ed9 b\u1eb1ng nhau:</br>\n              d1.S.h1 = d2.S.h2 (2)</br>\n              136000.h1 = 10000.h2</br>\n              => h2=13600/10000.h1 = 13,6.h1 (2)</br> \n              Thay (2) v\xe0o (1) ta \u0111\u01b0\u1ee3c:</br>\n              h1 + 13,6.h1 = 29,2</br>\n              14,6.h1 = 29,2</br>\n              h1 = 29,2/14,6 = 2,1 cm (3)</br>\n              Thay (3) v\xe0o (1) ta \u0111\u01b0\u1ee3c:</br>\n              2,1 + h2 = 29,2</br>\n              h2 = 29,2 - 2,1 = 27,1 cm (4)</br>              \n              \xc1p su\u1ea5t th\u1ee7y ng\xe2n t\xe1c d\u1ee5ng l\xean \u0111\xe1y \u1ed1ng l\xe0:</br>\n              p1 = d1.h1 = 136000.0,021 = 2856 N/m2</br>\n              \xc1p su\u1ea5t n\u01b0\u1edbc t\xe1c d\u1ee5ng l\xean \u0111\xe1y \u1ed1ng l\xe0:</br>\n              p2 = d2.h2 = 10000.0,271 = 2710 N/m2</br>\n              \xc1p su\u1ea5t t\u1ed5ng c\u1ed9ng c\u1ee7a hai ch\u1ea5t l\u1ecfng t\xe1c d\u1ee5ng l\xean \u0111\xe1y \u1ed1ng l\xe0:</br>\n              p = p1 + p2 = 2856 + 2710 = 5566 N/m2\n              </p>"},{id:41,extra:"",question:"<p>M\u1ed9t t\xe0u ng\u1ea7m \u0111ang di chuy\u1ec3n d\u01b0\u01a1i m\u1eb7t n\u01b0\u1edbc, \xe1p k\u1ebf \u0111\u1eb7t ngo\xe0i v\u1ecf t\xe0u ch\u1ec9 \xe1p su\u1ea5t 2020000 N/m2, m\u1ed9t l\xfac sau \xe1p k\u1ebf ch\u1ec9 860000N/m2. \u0110\u1ed9 s\xe2u c\u1ee7a t\xe0u ng\u1ea7m \u1edf 2 th\u1eddi \u0111i\u1ec3m l\xe0 bao nhi\xeau? bi\u1ebft tr\u1ecdng l\u01b0\u1ee3ng ri\xeang c\u1ee7a n\u01b0\u1edbc bi\u1ec3n l\xe0 10300N/m3</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>\n              \xc1p su\u1ea5t \u1edf \u0111\u1ed9 sau ha \u0111\u01b0\u1ee3c t\xednh theo c\xf4ng th\u1ee9c: p=d.h</br>\n              \u0110\u1ed9 s\xe2u c\u1ee7a t\xe0u ng\u1ea7m l\xfac \u0111\u1ea7u l\xe0:</br>\n              h1 = p1/d = 2020000/10300 = 196,1 m</br>\n              \u0110\u1ed9 s\xe2u c\u1ee7a t\xe0u ng\u1ea7m l\xfac sau l\xe0:</br>\n              h2 = p2/d = 860000/10300 = 83,5 m</br>              \n              </p>"},{id:41,extra:"",question:"<p>M\u1ed9t b\xecnh th\xf4ng nhau ch\u1ee9a n\u01b0\u1edbc bi\u1ec3n, ng\u01b0\u1eddi ta \u0111\u1ed5 th\xeam x\u0103ng v\xe0o m\u1ed9t nh\xe1nh. Hai m\u1eb7t tho\xe1ng ch\xeanh l\u1ec7ch nhau 18 mm. \u0110\u1ed9 cao c\u1ee7a c\u1ed9t x\u0103ng l\xe0 bao nhi\xeau? Bi\u1ebft tr\u1ecdng l\u01b0\u1ee3ng ri\xeang c\u1ee7a n\u01b0\u1edbc bi\u1ec3n l\xe0 10300N/m3, v\xe0 x\u0103ng l\xe0 7000N/m3</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p>\n              <img alt=\'1\' src="/learn/ly/Baitap4_7.png" style="max-width:100% !important;"/>\n              </p>'},{id:41,extra:"",question:"<p>M\u1ed9t th\u1ee3 l\u1eb7n xu\u1ed1ng \u0111\u1ed9 s\xe2u 40m so v\u1edbi m\u1ef1c n\u01b0\u1edbc bi\u1ec3n. Cho tr\u1ecdng lu7o5ng ri\xeang c\u1ee7a n\u01b0\u1edbc bi\u1ec3n 10300 N/m2. </br>\n              a. T\xednh \xe1p su\u1ea5t \u1edf \u0111\u1ed9 s\xe2u m\xe0 ng\u01b0\u1eddi th\u1ee3 \u0111ang l\u1eb7n l\xe0 bao nhi\xeau?</br>\n              b. C\u1eeda chi\u1ebfu s\xe1ng c\u1ee7a \xe1o l\u1eb7n c\xf3 di\u1ec7n t\xedch l\xe0 0.016 m2. \xc1p l\u1ef1c c\u1ee7a n\u01b0\u1edbc t\xe1c d\u1ee5ng l\xean ph\u1ea7n di\u1ec7n t\xedch n\xe0y l\xe0 bao nhi\xeau?</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>\n              a. \xc1p su\u1ea5t c\u1ee7a th\u1ee3 l\u1eb7n \u1edf \u0111\xf4 s\xe2u 40m: p=d.h =10300 . 40 = 412000 N/m2</br>\n              b. \xc1p l\u1ef1c c\u1ee7a n\u01b0\u1edbc t\xe1c d\u1ee5ng l\xean c\u1eeda chi\u1ebfu s\xe1ng: F=p.S = 412000 * 0.016 = 6592 N</p>"},{id:41,extra:"",question:"<p>T\xe1c d\u1ee5ng m\u1ed9t l\u1ef1c 480 N l\xean m\u1ed9t pitong nh\u1ecf c\u1ee7a m\xe1y \xe9p d\xf9ng n\u01b0\u1edbc. Di\u1ec7n t\xedch c\u1ee7a pitong nh\u1ecf l\xe0 2.5 cm2, di\u1ec7n t\xedch c\u1ee7a pitogng l\u1edbn l\xe0 200 cm2, \xe1p su\u1ea5t t\xe1c d\u1ee5ng l\xean pitong nh\u1ecf v\xe0 pitong l\u1edbn l\xe0 bao nhi\xeau?</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p><img alt=\'1\' src="/learn/ly/Baitap4_8_1.png" style="max-width:100% !important;"/></p>'},{id:41,extra:"",question:"<p>M\u1ed9t c\xe1i \u0111\u1eadp n\u01b0\u1edbc c\u1ee7a nh\xe0 m\xe1y th\u1ee7y \u0111i\u1ec7n c\xf3 chi\u1ec1u cao t\u1eeb \u0111\xe1y h\u1ed3 ch\u1ee9a n\u01b0\u1edbc \u0111\u1ebfn m\u1eb7t \u0111\u1eadp l\xe0 150 m. Kho\u1ea3ng c\xe1ch t\u1eeb m\u1eb7t \u0111\u1eadp \u0111\u1ebfm m\u1eb7t n\u01b0\u1edbc l\xe0 20 m , c\u1eeda van d\u1eabn n\u01b0\u1edbc v\xe0o tua bin c\u1ee7a m\xe1y ph\xe1t \u0111i\u1ec7n c\xe1ch \u0111\xe1y h\u1ed3 l\xe0 30 m. T\xednh \xe1p su\u1ea5t c\u1ee7a n\u01b0\u1edbc t\xe1c d\u1ee5ng l\xean c\u1eeda van, bi\u1ebft tr\u1ecdng l\u01b0\u1ee3ng ri\xeang c\u1ee7a n\u01b0\u1edbc l\xe0 10000 N/m3. </p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:"<p>\u0110\xf4 s\xe2u c\u1ee7a c\u1eeda van so v\u1edbi m\u1eb7t n\u01b0\u1edbc l\xe0: 150 - 30 -20 =100m </br>\n              \xc1p su\u1ea5t c\u1ee7a n\u01b0\u1edbc t\xe1c d\u1ee5ng l\xean van l\xe0 p=d*h = 1000 * 100 =100000 N/m2</p>"},{id:41,extra:"",question:'<p>Hai b\xecnh h\xecnh tr\u1ee5 A v\xe0 B \u0111\u1eb7t th\u1eb3ng \u0111\u1ee9ng c\xf3 ti\u1ebft di\u1ec7n l\u1ea7n l\u01b0\u1ee3t l\xe0 150cm2 v\xe0 300cm2 \u0111\u01b0\u1ee3c n\u1ed1i th\xf4ng \u0111\xe1y b\u1eb1ng m\u1ed9t \u1ed1ng nh\u1ecf qua kho\xe1 k nh\u01b0 h\xecnh v\u1ebd. L\xfac \u0111\u1ea7u kho\xe1 k \u0111\u1ec3 ng\u0103n c\xe1ch hai b\xecnh, sau \u0111\xf3 \u0111\u1ed5 v (cm3) d\u1ea7u v\xe0o b\xecnh A, \u0111\u1ed5 v (cm3) n\u01b0\u1edbc v\xe0o b\xecnh B.</br>\n              <img alt=\'1\' src="/learn/ly/Baitap4_9.png" style="max-width:100% !important;"/></br/>\n              Cho bi\u1ebft tr\u1ecdng l\u01b0\u1ee3ng ri\xeang c\u1ee7a d\u1ea7u v\xe0 c\u1ee7a n\u01b0\u1edbc l\u1ea7n l\u01b0\u1ee3t l\xe0: d1=8000N/m3 ; d2= 10000N/m3. K\u1ebft lu\u1eadn n\xe0o sau \u0111\xe2y l\xe0 ch\xednh x\xe1c?</br>\nA. \xe1p su\u1ea5t \u0111\xe1y b\xecnh A b\u1eb1ng \xe1p su\u1ea5t \u0111\xe1y b\xecnh B v\xec th\u1ec3 t\xedch ch\u1ea5t l\u1ecfng \u1edf hai b\xecnh l\xe0 nh\u01b0 nhau</br>\nB. \xe1p su\u1ea5t \u0111\xe1y b\xecnh A b\u1eb1ng \xe1p su\u1ea5t \u0111\xe1y b\xecnh B</br>\nC. \xe1p su\u1ea5t \u0111\xe1y b\xecnh A l\u1edbn h\u01a1n \xe1p su\u1ea5t \u0111\xe1y b\xecnh B</br>\nD. \xe1p su\u1ea5t \u0111\xe1y b\xecnh A nh\u1ecf h\u01a1n \xe1p su\u1ea5t \u0111\xe1y b\xecnh B\n              </p>\n        ',options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p><img alt=\'1\' src="/learn/ly/Baitap4_9_1.png" style="max-width:100% !important;"/></p>'},{id:41,extra:"",question:"<p>M\u1ed9t b\xecnh th\xf4ng nhau c\xf3 ch\u1ee9a n\u01b0\u1edbc. Hai nh\xe1nh c\u1ee7a b\xecnh c\xf3 c\xf9ng k\xedch th\u01b0\u1edbc. \u0110\u1ed5 v\xe0o m\u1ed9t nh\xe1nh c\u1ee7a b\xecnh l\u01b0\u1ee3ng d\u1ea7u c\xf3 chi\u1ec1u cao l\xe0 18 cm. Bi\u1ebft tr\u1ecdng l\u01b0\u1ee3ng ri\xeang c\u1ee7a d\u1ea7u l\xe0 8000 N/m3, v\xe0 tr\u1ecdng l\u01b0\u1ee3ng ri\xeang c\u1ee7a n\u01b0\u1edbc l\xe0 10000 N/m3. H\xe3y t\xednh \u0111\u1ed9 ch\xeanh l\u1ec7ch m\u1ef1c ch\u1ea5t l\u1ecfng trong hai nh\xe1nh c\u1ee7a b\xecnh ?</p>\n        ",options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p><img alt=\'1\' src="/learn/ly/Baitap4_10_1.png" style="max-width:100% !important;"/></p>'},{id:41,extra:"",question:'<p><img alt=\'1\' src="/learn/ly/Baitap4_11.png" style="max-width:100% !important;"/></p>\n        ',options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p><img alt=\'1\' src="/learn/ly/Baitap4_11_1.png" style="max-width:100% !important;"/></p>'},{id:41,extra:"",question:'<p><img alt=\'1\' src="/learn/ly/Baitap4_12.png" style="max-width:100% !important;"/></p>\n        ',options:[],answer:"",rewrite:"",lastwrite:"",explanation:'<p><img alt=\'1\' src="/learn/ly/Baitap4_12_1.png" style="max-width:100% !important;"/></p>'}]}]}}]);
